import React from 'react';

interface HeaderProps {
  darkMode: boolean;
  toggleDarkMode: () => void;
}

const Header: React.FC<HeaderProps> = ({ darkMode, toggleDarkMode }) => {
  return (
    <header className="bg-gray-800 text-white p-4 dark:bg-gray-200 dark:text-gray-800"> 
    <div className="container mx-auto flex justify-between items-center">
      <h1 className="text-2xl font-bold">Benjamin Ayesu-Attah</h1>
        <nav>
          <ul className="flex space-x-4">
            <li><a href="#hero">Home</a></li>
            <li><a href="#about">About</a></li>
            <li><a href="#projects">Projects</a></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
          <label htmlFor="dark-mode-toggle" className="flex items-center cursor-pointer ml-4"> 
            <div className="relative">
              <input type="checkbox" id="dark-mode-toggle" className="sr-only" checked={darkMode} onChange={toggleDarkMode} />
              <div className={`w-10 h-4 bg-gray-400 rounded-full shadow-inner ${darkMode ? 'bg-blue-500' : ''}`}>
                <div className={`dot absolute w-6 h-6 bg-white rounded-full shadow -left-1 -top-1 transition ${darkMode ? 'translate-x-full' : ''}`}></div>
              </div>
            </div>
          </label>
        </nav>
      </div>
    </header>
  );
}

export default Header;
